import React, {useState} from 'react';

import ExpensesFilter from './ExpensesFilter';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import './ExpensesFilter.css';

// store the selected filter year in a state in this file

const Expenses = (props) => {
    const [filteredYear, setFilteredYearValue] = useState('2020');

    const filterByYearHandler = selectedYear => {
        setFilteredYearValue(selectedYear);
        const result = props.items.filter(item => {
            const daniYear = item.date.getFullYear();

            if (daniYear === selectedYear) {

            return <ExpenseItem
                key={item.id}
                title={item.title}
                amount={item.amount}
                date={item.date}
            />
            }

        });
        var x = 10;
    };
    return (
        <Card className="expenses">
            <ExpensesFilter selected={filteredYear} onChangeFilter={filterByYearHandler} />
            {props.items.map((expense) => {
                return <ExpenseItem
                    key={expense.id}
                    title={expense.title}
                    amount={expense.amount}
                    date={expense.date}
                />
            })}
        </Card>
    );
};

export default Expenses;
