import React, {useState} from 'react';

import ExpensesFilter from './ExpensesFilter';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import './ExpensesFilter.css';

// store the selected filter year in a state in this file

const Expenses = (props) => {
    const [filteredYear, setFilteredYearValue] = useState('2020');

    const filterByYearHandler = selectedYear => {
        setFilteredYearValue(selectedYear);
    };

    const filteredExpenses = props.items.filter(expense => {
        return new Date(expense.date).getFullYear().toString() === filteredYear
    });

    let expenseContent = <p>No expenses records found.</p>;

    if (filteredExpenses.length > 0) {
        expenseContent = filteredExpenses.map((expense) => {
            return <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
            />
        });
    }

    return (
        <Card className="expenses">
            <ExpensesFilter selected={filteredYear} onChangeFilter={filterByYearHandler} />
            {expenseContent}
        </Card>
    );
};

export default Expenses;
