import React, {useState} from 'react';

import ExpensesFilter from './ExpensesFilter';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import './ExpensesFilter.css';

// store the selected filter year in a state in this file

const Expenses = props => {
    const [year, setYearValue] = useState('');

    const filterByYearHandler = selectedYear => {
        console.log('Expenses.js');
        console.log('The selected year is: ' + selectedYear);
        setYearValue(selectedYear)
    };
    return (
        <Card className="expenses">
            <div><ExpensesFilter onFilterByYear={filterByYearHandler} /></div>
            <ExpenseItem
                title={props.items[0].title}
                amount={props.items[0].amount}
                date={props.items[0].date}
            />
            <ExpenseItem
                title={props.items[1].title}
                amount={props.items[1].amount}
                date={props.items[1].date}
            />
            <ExpenseItem
                title={props.items[2].title}
                amount={props.items[2].amount}
                date={props.items[2].date}
            />
            <ExpenseItem
                title={props.items[3].title}
                amount={props.items[3].amount}
                date={props.items[3].date}
            />
        </Card>
    );
};

export default Expenses;
